function cartRemove(prid_str, url){ if(confirm("确定从购物车中删除此商品？")){ jQuery.post(url,{ajax : 'true' ,action : 'remove_product' , products_id : prid_str } , function(data){ if(data){ if(data['removeSuccess'] == "1"){ var tr = document.getElementById('J_catrTr_'+prid_str); $(tr).fadeOut("500",function(o){ $(o).remove(); $('#J_cartTotal').html(data['J_cartTotal']); }); } } }, "json"); } }